$(function() {
    window.keydown = {};

    $(document).bind("keydown", function(b) {
        if (b.keyCode == keyCodeDefault[0] || b.keyCode == keyCodeDefault[1] || b.keyCode == keyCodeDefault[2] || b.keyCode == keyCodeDefault[3] || b.keyCode == keyCodeDefault[4]) {
            keydown[b.keyCode] = true;
        }
    });

    $(document).bind("keyup", function(b) {
        if (b.keyCode == keyCodeDefault[0] || b.keyCode == keyCodeDefault[1] || b.keyCode == keyCodeDefault[2] || b.keyCode == keyCodeDefault[3] || b.keyCode == keyCodeDefault[4]) {
            keydown[b.keyCode] = false;
        }
    });
});